mapper.insert:
insert into spa_dir(id, title, thumbnail, root, home, personal, modifiable, creator_id, created_at) values (
#{dir.id}, #{dir.title}, #{dir.thumbnail}, #{dir.root}, #{dir.home}, #{dir.personal}, #{dir.modifiable}, #{dir.creator_id},
#{dir.created_at});

mapper.select:
with RECURSIVE r as
(
  select * from master_salves where master_id = #{master_id}
  union all
  select master_slaves.* from master_slaves, r where master_slaves.master_id = r.slave_id
) select r.slave_id from r;

with RECURSIVE r as
(
  select * from master_slaves where slave_id = #{slave_id}
  union all
  select master_slaves.* from master_slaves, r where master_slaves.slave_id = r.master_id
)
select d.id, d.title from spa_dir d inner join r on r.master_id = d.id;
